import React from "react";
import "../App.scss"

export const Card = (props) => {
    const {infoAbout} = props;
    return (
        <div className="card" id="card-about">
         <a href={infoAbout.lien}>  <img src={infoAbout.socialMedia} 
            className="card-img-top" 
            alt="Sebastien" />  
          </a> 
           
          </div>
    ) 
};

export const CardCompetence = (props) => {
    const {infoCompetence} = props;
  
    return (
        <div className="card" id='cardCompetence'>
         <img src={infoCompetence.socialMedia} 
            className="card-img-top" 
            alt={infoCompetence.altImage}  />  
        </div>
    )
  }

export const ListSocialMedias = (props) =>{
    const {data} = props;
    const aboutInformation = data[0]
    console.log(aboutInformation)
      
      return (
          <div className="container">
             <div className="row" id='rowAbout'>
            {aboutInformation.map(infoAbout => <Card key={infoAbout.id}  infoAbout={infoAbout} /> )}
            </div>
          </div>
      )
}
